async (qty, itemId) => {
  let cart = appsmith.store.cart;
  let cartRow = cart.findIndex(item => item.id == itemId);
  cart[cartRow].qty = qty;
  await storeValue('cart', cart);
  await resetWidget('lst_cart');
}

